import React, {useState} from 'react'
import './Experience.css'
import { createMuiTheme, ThemeProvider, StylesProvider } from '@material-ui/core/styles';
import Title from './Title'
import Button from '@material-ui/core/Button';
import ToggleButton from '@material-ui/lab/ToggleButton';
import ToggleButtonGroup from '@material-ui/lab/ToggleButtonGroup';

// Experience Section

const Experience = (props) => {
    const [alignment, setAlignment] = React.useState(1);
    const styles  = {
        ToggleContainer:{
            Button: {
                    background: "transparent",
                    display: "block",
                    margin: "1rem",
                    border: "1px solid gray"
            }, 
            ToggleButtonGroup:{
                background:"transparent",  
                display: "block",
            },
        },
        divShowTask: {
            ul: {
                background: "rgb(33,45,57)",

            },
            li :{
                display: "block",
                color: "honeydew"
            }
        }
    }
    const handleAlignment = (event, newAlignment) => {
    setAlignment(newAlignment);
  };
  const experience = props.experience
    return (
        <div className="containerExperience">
        <Title  index={"03. "} titleName={"Where I've worked"} />
        <ToggleButtonGroup
            style={styles.ToggleContainer.ToggleButtonGroup}
            value={alignment}
            exclusive
            onChange={handleAlignment}
            aria-label="text alignment"
            >
            {experience.map(index => {
                return (
               <>
                <Button  style={styles.ToggleContainer.Button} value={index.key}> {index.name} 
                </Button>
                <div className="collapse" style={styles.divShowTask.ul}>
                    <Title index={index.date} size={'1rem'}/>
                    <ul >
                        {
                            index.desc.map((i) => {
                                return (
                                <li style={styles.divShowTask.li}> {i.task} </li>
                                )
                            })
                        }
                    </ul>
                </div>
                </>
                )
            })}
           </ToggleButtonGroup>
           </div>
    )
}
export default Experience;
