import React  from 'react'
import  logo from '../data/img/profilePicture.jpg'
import Education from './Education'
import Button from '@material-ui/core/Button';
import { FaRegPaperPlane  } from "react-icons/fa";
import './Greeting.css'
import { FaFileContract } from 'react-icons/fa';
const  Greeting = (props) => {
const about = props.resumeAbout
const name = props.resumeName
const email = props.resumeEmail
const styles = {
    contactButton: {
        background: "transparent",
        border: "1px solid gray" 
    }
}
const contact = (event ) => {
    document.getElementById(event.currentTarget.id).innerText = email.toLowerCase();
    document.getElementById(event.currentTarget.id).style.cssText = 
       "background-color:white; border-radius:1rem; color:red" 
}
    return (
            // Picture of me 
        <div id="containerGreeting" >
            <img  id="profileMe" src= {logo}   />   
            <h3  id="mynameis"> Hi, my name is </h3>
            <h2 id="name"> {name} </h2>
            <h1 id="about"> Fullsnack Engineer | Software Engineer   </h1>
            <h3 id="school"> I am currently enjoy my senior year at  <span id="university"> University of Texas at San Antonio </span></h3>
            <Button id="contactButton"  onMouseEnter={(event)=> contact(event)} variant="contained" color="secondary">
                 GET IN TOUCH  &nbsp; <FaRegPaperPlane/>
            </Button>
        </div>
    )
}
export default Greeting;
