import React, {useState} from 'react'
import './Experience.css'
import Title from './Title'
import Button from '@material-ui/core/Button';
// Experience Section

const Experience = (props) => {
    const [style, setStyle] = useState(true);
    const styles = {
        buttonPrimary: {
            display: "block",
            width: "fit-content",
            color: "light gray", 
        },

        buttonSecondary: {
            width: "fit-content",
            background: "linear-gradient(45deg, #FE6B8B 30%, #FF8E53 90%)", 
        },
        containerListCompanies: {
            textAlign: "center",
        }, 
    }
    const experience = props.experience
    const changeHandler = (event) => {
        setStyle(!style)
    }
    return (
        <div className="containerExperience">
        <Title  index= {"03."} titleName={"Where I've worked"} />
            <div className="experienceList">
                <ul style={styles.containerListCompanies}>
                    {experience.map(index => {
                    return <Button style={style == true ? styles.buttonPrimary : styles.buttonSecondary } onClick={(event)=> changeHandler(event)} 
                    style={styles.buttonPrimary} > {index.name} 
                    </Button>
                    })}
                </ul>
            </div>
        </div>
    )
}
export default Experience;
