import React, {useState} from 'react'
import './Experience.css'
import { createMuiTheme, ThemeProvider, StylesProvider } from '@material-ui/core/styles';
import Title from './Title'
import Button from '@material-ui/core/Button';
import ToggleButton from '@material-ui/lab/ToggleButton';
import ToggleButtonGroup from '@material-ui/lab/ToggleButtonGroup';

// Experience Section

const Experience = (props) => {
    const [alignment, setAlignment] = React.useState(1);
    const styles  = {
        Button: {
            background: "transparent",
            display: "block",
            margin: "1rem",
            width: "100%",
            border: "1px solid gray"
        }
    }
    const handleAlignment = (event, newAlignment) => {
    setAlignment(newAlignment);
  };
  const experience = props.experience
    return (
        <div className="containerExperience">
        <Title  index={"03. "} titleName={"Where I've worked"} />
        <ToggleButtonGroup style={{background:"transparent",width:"fit-content",  display: "block"}}>
            {experience.map(index => {
                return <Button  style={styles.Button} value={index.key}> {index.name} </Button>
            })}

           </ToggleButtonGroup>

           </div>
    )
}
export default Experience;
