import React, {useState} from 'react'
import './Experience.css'
import Title from './Title'
import Button from '@material-ui/core/Button';
// Experience Section

const Experience = (props) => {
    const [style, setStyle] = useState(true);
    const styles = {
        buttonPrimary: {
            display: "block",
            width: "fit-content",
            color: "light gray", 
            textAlign: "center",
            borderLeft: "red",
        }, 
        buttonSecondary: {
            width: "fit-content",
        }
    }
    const experience = props.experience
    const changeHandler = (event) => {
        document.getElementById(event.currentTarget.id).style.cssText="background:gray; borderLeft: green; opacity:0.2";
    }
    return (
        <div className="containerExperience">
        <Title  index= {"03."} titleName={"Where I've worked"} />
            <div className="experienceList">
                <ul>
                    {experience.map(index => {
                    return <Button id={index.name} onClick={(event)=> changeHandler(event)} 
                    style={styles.buttonPrimary} > {index.name} 
                    </Button>
                    })}
                </ul>
            </div>
        </div>
    )
}
export default Experience;
